mapper.select:
select

  distinct


from zip_order_form




  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

update.foreach:
update zip_order_form
order_date=#{item.orderDate,jdbcType=TIMESTAMP}, commodity_id=#{item.commodityId,jdbcType=INTEGER}, commodity_name=#{item.commodityName,jdbcType=VARCHAR}, commodity_picture=#{item.commodityPicture,jdbcType=VARCHAR}, wechat_user_id=#{item.wechatUserId,jdbcType=INTEGER}, address_id=#{item.addressId,jdbcType=INTEGER}, address=#{item.address,jdbcType=VARCHAR}, number=#{item.number,jdbcType=INTEGER}, price=#{item.price,jdbcType=DOUBLE}, total_amount=#{item.totalAmount,jdbcType=DOUBLE}, express=#{item.express,jdbcType=DOUBLE}, order_number=#{item.orderNumber,jdbcType=VARCHAR}, tracking_type=#{item.trackingType,jdbcType=VARCHAR}, tracking_number=#{item.trackingNumber,jdbcType=VARCHAR}, express_type=#{item.expressType,jdbcType=INTEGER}, status=#{item.status,jdbcType=INTEGER}, sent_time=#{item.sentTime,jdbcType=TIMESTAMP}, pay_time=#{item.payTime,jdbcType=TIMESTAMP}, texture_id=#{item.textureId,jdbcType=INTEGER}, technology=#{item.technology,jdbcType=INTEGER}, optimal_scale=#{item.optimalScale,jdbcType=INTEGER}, create_time=#{item.createTime,jdbcType=TIMESTAMP}, update_time=#{item.updateTime,jdbcType=TIMESTAMP},
where id = #{item.id,jdbcType=INTEGER}

mapper.select:
select ff.*,z.texture_id,z.technology from (
select f.*,u.nick_name,u.open_id from zip_order_form f left join zip_wechat_user u on  f.wechat_user_id = u.id

  status !=-1

    and  DATE_FORMAT(f.create_time,'%y-%m-%d') >= DATE_FORMAT(#{sTime},'%y-%m-%d')


    and   DATE_FORMAT(#{eTime},'%y-%m-%d')>= DATE_FORMAT(f.create_time,'%y-%m-%d')


    and f.status = #{status}


    and f.wechat_user_id in (select w.id from zip_wechat_user w where w.nick_name like CONCAT('%', #{wechatUserName} , '%'))


    and f.order_number like CONCAT('%', #{orderNumber} , '%')


    and f.wechat_user_id = #{wechatUserId}


) ff left join zip_commodity z on ff.commodity_id = z.id order by ff.optimal_scale,ff.create_time desc
limit #{start},#{end}

select count(*) from (
select f.*,u.nick_name,u.open_id from zip_order_form f left join zip_wechat_user u on  f.wechat_user_id = u.id

  status !=-1

    and  DATE_FORMAT(f.create_time,'%y-%m-%d') >= DATE_FORMAT(#{sTime},'%y-%m-%d')


    and   DATE_FORMAT(#{eTime},'%y-%m-%d')>= DATE_FORMAT(f.create_time,'%y-%m-%d')


    and f.status = #{status}


    and f.wechat_user_id in (select w.id from zip_wechat_user w where w.nick_name like CONCAT('%', #{wechatUserName} , '%'))


    and f.order_number like CONCAT('%', #{orderNumber} , '%')


    and f.wechat_user_id = #{wechatUserId}


) ff left join zip_commodity z on ff.commodity_id = z.id
